n, m = list(map(int, input().split()))

s = []

for i in range(n):
	s.append(input())

ans = 0

for i in range(n):
	for j in range(m):
		if s[i][j] == '1':
			ans += 1

		else:
			x = min(ans, (m - j) * (n - i) // (m - 1) + (n - i) * (m - j) // (n - 1))
			y = min(ans, (m - i) * (n - j) // (m - 1) + (n - i) * (m - j) // (n - 1))
			ans = min(x, y)

print(ans)
